#!/bin/bash

VAGRANT_HOME=/home/vagrant
AUTHORIZED_KEY_FILE=$VAGRANT_HOME/.ssh/authorized_keys

date > /etc/vagrant_box_build_time

#groupadd -g 500 vagrant
#useradd -u 500 -g 500 -d /home/vagrant -m -s /bin/bash vagrant
mkdir -p $VAGRANT_HOME/.ssh
cat <<EOF > "$AUTHORIZED_KEY_FILE"
ssh-rsa  vagrant insecure public key
EOF
chown -R vagrant:vagrant /home/vagrant/.ssh/
chmod -R 700 $VAGRANT_HOME/.ssh
chmod -R 600 $AUTHORIZED_KEY_FILE
